def run(data_store, helpers, definition):
    alerts = []
    excluded_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        grouped = {}
        for relation in relations:
            role_raw = relation.get("Roles")
            role_norm = helpers.normalize(role_raw)
            if not role_norm or role_norm == excluded_role:
                continue
            contact_id = relation.get("ContactId")
            contact = data_store.contacts.get(contact_id)
            if not contact:
                continue
            first = helpers.normalize(contact.get("FirstName"))
            last = helpers.normalize(contact.get("LastName"))
            name_key = f"{first} {last}".strip()
            if not name_key:
                continue
            entry = {
                "contact": contact,
                "fiscal_norm": helpers.normalize(contact.get("FiscalCode__c")),
                "fiscal_raw": contact.get("FiscalCode__c").strip() if isinstance(contact.get("FiscalCode__c"), str) else contact.get("FiscalCode__c"),
                "vat_norm": helpers.normalize(contact.get("VATNumber__c")),
                "vat_raw": contact.get("VATNumber__c").strip() if isinstance(contact.get("VATNumber__c"), str) else contact.get("VATNumber__c"),
            }
            grouped.setdefault((role_norm, name_key), []).append(entry)
        for (role_norm, name_key), entries in grouped.items():
            duplicates = []
            fiscal_groups = {}
            for entry in entries:
                if entry["fiscal_norm"]:
                    fiscal_groups.setdefault(entry["fiscal_norm"], []).append(entry)
            for norm, matches in fiscal_groups.items():
                if len(matches) > 1:
                    duplicates.append(("codice fiscale", matches[0]["fiscal_raw"], [m["contact"] for m in matches]))
            vat_groups = {}
            for entry in entries:
                if entry["vat_norm"]:
                    vat_groups.setdefault(entry["vat_norm"], []).append(entry)
            for norm, matches in vat_groups.items():
                if len(matches) > 1:
                    duplicates.append(("partita IVA", matches[0]["vat_raw"], [m["contact"] for m in matches]))
            for label, value, contacts in duplicates:
                account_name = helpers.get_account_name(data_store, account_id)
                role_label = helpers.title_case(role_norm)
                contact_names = ", ".join(sorted({helpers.format_contact_name(c) for c in contacts}))
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Account '{account_name}' ha più contatti ({contact_names}) con ruolo '{role_label}' e lo stesso {label}: {value}.",
                    }
                )
    return alerts

