def run(data_store, helpers, definition):
    alerts = []
    excluded_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        account_name = helpers.get_account_name(data_store, account_id)
        for relation in relations:
            role_norm = helpers.normalize(relation.get("Roles"))
            if role_norm == excluded_role:
                continue
            contact = data_store.contacts.get(relation.get("ContactId"))
            if not contact:
                continue
            values = [contact.get("MobilePhone"), contact.get("HomePhone"), contact.get("Email")]
            has_value = any(
                (isinstance(value, str) and value.strip()) or (not isinstance(value, str) and value)
                for value in values
                if value is not None
            )
            if not has_value:
                contact_name = helpers.format_contact_name(contact)
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' non ha telefono fisso/mobile né e-mail valorizzati.",
                    }
                )
    return alerts

def run(data_store, helpers, definition):
    alerts = []
    excluded_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        grouped = {}
        for relation in relations:
            role_raw = relation.get("Roles")
            role_norm = helpers.normalize(role_raw)
            if not role_norm or role_norm == excluded_role:
                continue
            contact = data_store.contacts.get(relation.get("ContactId"))
            if not contact:
                continue
            first = helpers.normalize(contact.get("FirstName"))
            last = helpers.normalize(contact.get("LastName"))
            name_key = (first, last)
            if not (first or last):
                continue
            grouped.setdefault(name_key, set()).add(role_norm)
        for (first, last), roles in grouped.items():
            if len(roles) > 1:
                account_name = helpers.get_account_name(data_store, account_id)
                formatted_name = f"{helpers.title_case(first)} {helpers.title_case(last)}".strip()
                role_list = ", ".join(sorted(helpers.title_case(role) for role in roles))
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Account '{account_name}' ha il contatto {formatted_name} con ruoli differenti: {role_list}.",
                    }
                )
    return alerts

def run(data_store, helpers, definition):
    alerts = []
    excluded_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        account_name = helpers.get_account_name(data_store, account_id)
        for relation in relations:
            role_norm = helpers.normalize(relation.get("Roles"))
            if role_norm == excluded_role:
                continue
            contact_id = relation.get("ContactId")
            contact = data_store.contacts.get(contact_id)
            if not contact:
                continue
            phone_values = {}
            for label, field in ("mobile", "MobilePhone"), ("fisso", "HomePhone"):
                raw = contact.get(field)
                if isinstance(raw, str) and raw.strip():
                    phone_values[label] = {
                        "raw": raw.strip(),
                        "norm": helpers.normalize_phone(raw),
                    }
            cp_phones = data_store.get_phones_for_contact(contact_id)
            cp_numbers = set()
            for phone in cp_phones:
                candidate = phone.get("TelephoneNumber") or phone.get("Phone")
                norm = helpers.normalize_phone(candidate)
                if norm:
                    cp_numbers.add(norm)
            contact_has_phone = any(value["norm"] for value in phone_values.values())
            cp_has_phone = bool(cp_numbers)
            contact_name = helpers.format_contact_name(contact)
            if contact_has_phone and not cp_has_phone:
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' ha telefono valorizzato ma nessun ContactPointPhone associato.",
                    }
                )
                continue
            if cp_has_phone and not contact_has_phone:
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' ha ContactPointPhone ma nessun telefono fisso o mobile valorizzato.",
                    }
                )
                continue
            missing = []
            for label, value in phone_values.items():
                if value["norm"] and value["norm"] not in cp_numbers:
                    missing.append((label, value["raw"]))
            if missing:
                descriptions = ", ".join(f"{label}: {raw}" for label, raw in missing)
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' ha telefoni non presenti nei ContactPointPhone: {descriptions}.",
                    }
                )
    return alerts

def run(data_store, helpers, definition):
    alerts = []
    excluded_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        account_name = helpers.get_account_name(data_store, account_id)
        for relation in relations:
            role_norm = helpers.normalize(relation.get("Roles"))
            if role_norm == excluded_role:
                continue
            contact_id = relation.get("ContactId")
            contact = data_store.contacts.get(contact_id)
            if not contact:
                continue
            email_raw = contact.get("Email")
            email_norm = helpers.normalize(email_raw)
            cp_emails = data_store.get_emails_for_contact(contact_id)
            cp_norms = set()
            for email in cp_emails:
                value = email.get("EmailAddress") or email.get("Email")
                norm = helpers.normalize(value)
                if norm:
                    cp_norms.add(norm)
            contact_has_email = bool(email_norm)
            cp_has_email = bool(cp_norms)
            contact_name = helpers.format_contact_name(contact)
            if contact_has_email and not cp_has_email:
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' ha un'e-mail valorizzata ma nessun ContactPointEmail associato.",
                    }
                )
                continue
            if cp_has_email and not contact_has_email:
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' ha ContactPointEmail ma nessuna e-mail sul contatto.",
                    }
                )
                continue
            if contact_has_email and email_norm not in cp_norms:
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"L'e-mail del contatto {contact_name} dell'account '{account_name}' non coincide con i ContactPointEmail caricati.",
                    }
                )
    return alerts

def run(data_store, helpers, definition):
    alerts = []
    target_role = helpers.normalize("Referente SOL-APP")
    for account_id, relations in data_store.account_to_contact_relations.items():
        account_name = helpers.get_account_name(data_store, account_id)
        for relation in relations:
            role_norm = helpers.normalize(relation.get("Roles"))
            if role_norm != target_role:
                continue
            contact_id = relation.get("ContactId")
            contact = data_store.contacts.get(contact_id)
            if not contact:
                continue
            cp_emails = data_store.get_emails_for_contact(contact_id)
            has_sol_email = False
            for email in cp_emails:
                type_norm = helpers.normalize(email.get("Type__c"))
                value = email.get("EmailAddress") or email.get("Email")
                if type_norm == helpers.normalize("E-mail SOL") and isinstance(value, str) and value.strip():
                    has_sol_email = True
                    break
            if not has_sol_email:
                contact_name = helpers.format_contact_name(contact)
                alerts.append(
                    {
                        "type": definition.get("label", "Alert"),
                        "message": f"Il contatto {contact_name} dell'account '{account_name}' con ruolo Referente SOL-APP non ha un ContactPointEmail di tipologia 'E-mail SOL'.",
                    }
                )
    return alerts
